mapper.update:
UPDATE
        menu_info
SET
        m_name= #{m_name}, m_price = #{m_price}, m_cat = #{m_cat}, m_intro = #{m_intro}, m_code = #{m_code}, m_img = #{m_img}
WHERE
        m_id = #{m_id};

UPDATE
        menu_add
SET
        a_name = #{a_name}, a_price= #{a_price}
WHERE
        a_id = #{a_id};
