mapper.update:
UPDATE 
        rest_info
SET
        r_lname = #{r_lname}, r_addr = #{r_addr}, r_dtad = #{r_dtad}, r_bname = #{r_bname}, r_tel = #{r_tel}, f_code = #{f_code}, r_intro = #{r_intro}, r_minprice = #{r_minprice}, r_img = #{r_img}
WHERE
        r_id = #{r_id}

UPDATE
        rest_open
SET
        r_opent = #{r_opent}, r_closet = #{r_closet}
WHERE
        r_id = #{r_id} AND w_code = #{w_code}
